import React, { useState, useEffect } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import moment from "moment";

const Write = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const postId = location.pathname.split("/")[2];
  const isEditing = postId !== "new";

  const [value, setValue] = useState("");
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);
  const [cat, setCat] = useState("");
  const [selectedFileName, setSelectedFileName] = useState("");

  useEffect(() => {
    if (isEditing) {
      // Si estamos editando, obtenemos los datos del post
      axios.get(`/posts/${postId}`)
        .then((response) => {
          const postData = response.data;
          setTitle(postData.title);
          setValue(postData.desc);
          setCat(postData.cat);
          // Si hay una imagen, puedes cargarla asÃ­ncronamente si lo deseas
          // setFile(null); // Asume que la imagen se carga por separado
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [isEditing, postId]);

  const upload = async () => {
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post("/upload", formData);
      return res.data;
    } catch (err) {
      console.log(err);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setSelectedFileName(selectedFile ? selectedFile.name : "");
  };

  const handleClick = async (e) => {
    e.preventDefault();
    const imgUrl = await upload();

    try {
      if (isEditing) {
        await axios.put(`/posts/${postId}`, {
          title,
          desc: value,
          cat,
          img: file ? imgUrl : "",
        });
      } else {
        await axios.post(`/posts/`, {
          title,
          desc: value,
          cat,
          img: file ? imgUrl : "",
          date: moment(Date.now()).format("YYYY-MM-DD HH:mm:ss"),
        });
      }
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="add">
      <div className="content">
        <input
          type="text"
          placeholder="TÃ­tulo del post"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <div className="editorContainer">
          <ReactQuill
            className="editor"
            theme="snow"
            value={value}
            onChange={setValue}
          />
        </div>
      </div>
      <div className="menu">
        <div className="item">
          <h1>{isEditing ? "EDITAR" : "PUBLICAR"}</h1>

          <input
            style={{ display: "none" }}
            type="file"
            id="file"
            name=""
            onChange={handleFileChange}
          />
          <label className="file" htmlFor="file">
            {selectedFileName || "Portada"}
          </label>
          <button
            type="button"
            onClick={() => {
              document.getElementById("file").click();
            }}
          >
            Seleccionar Portada
          </button>
          </div>

          <div className="buttons">
          <button onClick={handleClick}>
            {isEditing ? "Guardar Cambios" : "Publicar"}
          </button>
        </div>
        </div>
        <div className="item">
          <h1>CategorÃ­as</h1>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria1"}
              name="cat"
              value="categoria1"
              id="categoria1"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria1">CATEGORIA1</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria2"}
              name="cat"
              value="categoria2"
              id="categoria2"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria2">CATEGORIA2</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria3"}
              name="cat"
              value="categoria3"
              id="categoria3"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria3">CATEGORIA3</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria4"}
              name="cat"
              value="categoria4"
              id="categoria4"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria4">CATEGORIA4</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria5"}
              name="cat"
              value="categoria5"
              id="categoria5"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria5">CATEGORIA5</label>
          </div>
          <div className="cat">
            <input
              type="radio"
              checked={cat === "categoria6"}
              name="cat"
              value="categoria6"
              id="categoria6"
              onChange={(e) => setCat(e.target.value)}
            />
            <label htmlFor="categoria6">CATEGORIA6</label>
          </div>
        </div>
      </div>

  );
};

export default Write;
